import{a as E,j as l,o as F,c as A}from"./entry.97c44b02.js";const B={name:"studios"};const C=l("<h2>Заказ сайта у студии довольно дорогое удовольствие - но почему же?</h2><p> Студии отлично справляются с разработкой сложного сайта или сервиса, так как у студий есть собственные методологии разработки и команды специалистов, которые отлично справляются с решением масштабных задач. Только в этом не только плюсы так как заказывая веб-сайт вы платите не за веб-сайт а за время нескольких разработчиков, а если говорить точнее то за время: </p><ul><li>Аккаунт-менеджер (средняя ЗП — 80 000);</li><li>Менеджер проекта (120 000);</li><li>Верстальщик (90 000);</li><li>Программисты (160 000);</li><li>тировщик (70 000);</li><li>SEO-оптимизатор (80 000);</li><li>Копирайтер (80 000).</li></ul><h2>Но зачем же мне эти разработчики?</h2><p> так как студии это проффесиональное решение то этапы создания сайта у них выглядят вот так </p><ol><li>Брифинг</li><li>Маркетинговое исследование рынка, сбор и систематизация информации</li><li>Разработка «рыбы» технического задания</li><li>Составление вопросов к техническому заданию</li><li>Разбор вопросов с клиентом очно, в Zoom или в Skype</li><li>Доработка технического задания</li><li>Финишное обсуждение технического задания</li><li>Финишная коррекция технического задания</li><li> Утверждение технического задания и закрытие этапа работ по составлению ТЗ актом </li><li>Прототипирование сервиса</li><li>Обсуждение прототипов с клиентом</li><li>Внесение доработок в прототипы</li><li> Утверждение прототипов и закрытие этапа работ по прототипированию актом </li><li>Разработка дизайна главной страницы ресурса</li><li>Обсуждение с клиентом дизайна главной страницы</li><li>Внесение доработок в дизайн главной страницы</li><li>Утверждение дизайна главной страницы</li><li>Разработка дизайна внутренних страниц</li><li>Обсуждение с клиентом</li><li>Внесение доработок в дизайн внутренних страниц</li><li>Утверждение дизайна и закрытие этапа работ по дизайну актом</li><li>Адаптивная верстка макетов</li><li>Разработка backend-части сайта</li><li>Разработка frontend-части сайта</li><li>Сборка всех компонентов сайта</li><li>Разворачивание сайта на тестовом стенде</li><li>Обсуждение с клиентом и составление перечня доработок</li><li>Внесение доработок</li><li>Финишное тестирование клиентом</li><li>Собственное тестирование разработки</li><li>Тестирование под нагрузкой (если необходимо)</li><li>Наполнение сайта контентом (опционально, если прописано в договоре)</li><li>Поисковая оптимизация (опционально, если прописано в договоре)</li><li>Конфигурирование хостинга или сервера</li><li> Написание руководства по использованию и администрированию разработки </li><li>Сдача в эксплуатацию</li><li> Гарантийная поддержка, обычно в течение 6 месяцев + доработки, если необходимо </li><li>Платная техническая поддержка, если необходима.</li></ol><p></p>",7);function e(D,i,u,c,s,_){return C}const t=E(B,[["render",e]]),n={};function o(D,i){const u=t;return F(),A(u)}const a=E(n,[["render",o]]);export{a as default};
